&lt;footer class=&quot;footer&quot;&gt;{{- if .Site.Copyright }}&lt;span&gt;{{ .Site.Copyright | markdownify }}&lt;/span&gt;{{- else }}&lt;span&gt;Copyright &amp;copy; {{ now.Year }} &lt;a href=&quot;{{ &quot;&quot; | absLangURL }}&quot;&gt;{{ .Site.Title }}&lt;/a&gt; All Rights Reserved&lt;/span&gt;{{- end }}&lt;span&gt;&lt;a href=&quot;https://beian.miit.gov.cn/&quot; target=&quot;_blank&quot;&gt;渝ICP备2022010337号&lt;/a&gt;&lt;/span&gt;&lt;/footer&gt;{{- if (not .Site.Params.disableScrollToTop) }}&lt;a href=&quot;#top&quot; aria-label=&quot;go to top&quot; title=&quot;Go to Top (Alt + G)&quot;&gt;&lt;button class=&quot;top-link&quot; id=&quot;top-link&quot; type=&quot;button&quot; accesskey=&quot;g&quot;&gt;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; viewBox=&quot;0 0 12 6&quot; fill=&quot;currentColor&quot;&gt;&lt;path d=&quot;M12 6H0l6-6z&quot; /&gt;&lt;/svg&gt;&lt;/button&gt;&lt;/a&gt;{{- end }}{{- partial &quot;extend_footer.html&quot; . -}}{{- $isHLJSdisabled := (.Site.Params.assets.disableHLJS | default .Params.disableHLJS ) }}{{- if (and (eq .Kind &quot;page&quot;) (ne .Layout &quot;archives&quot;) (ne .Layout &quot;search&quot;) (not $isHLJSdisabled)) }}{{- if not .Site.Params.assets.disableFingerprinting }}{{- $highlight := slice (resources.Get &quot;js/highlight.min.js&quot;) | resources.Concat &quot;assets/js/highlight.js&quot; | minify | fingerprint }}&lt;script defer src=&quot;{{ $highlight.RelPermalink }}&quot; integrity=&quot;{{ $highlight.Data.Integrity }}&quot;onload=&quot;hljs.initHighlightingOnLoad();&quot;&gt;&lt;/script&gt;{{- else}}{{- $highlight := slice (resources.Get &quot;js/highlight.min.js&quot;) | resources.Concat &quot;assets/js/highlight.js&quot; | minify }}&lt;script defer src=&quot;{{ $highlight.RelPermalink }}&quot; onload=&quot;hljs.initHighlightingOnLoad();&quot;&gt;&lt;/script&gt;{{- end}}{{- end }}&lt;script&gt;window.onload = function () {if (localStorage.getItem(&quot;menu-scroll-position&quot;)) {document.getElementById('menu').scrollLeft = localStorage.getItem(&quot;menu-scroll-position&quot;);}}function menu_on_scroll() {localStorage.setItem(&quot;menu-scroll-position&quot;, document.getElementById('menu').scrollLeft);}document.querySelectorAll('a[href^=&quot;#&quot;]').forEach(anchor =&gt; {anchor.addEventListener(&quot;click&quot;, function (e) {e.preventDefault();var id = this.getAttribute(&quot;href&quot;).substr(1);if (!window.matchMedia('(prefers-reduced-motion: reduce)').matches) {document.querySelector(`[id='${decodeURIComponent(id)}']`).scrollIntoView({behavior: &quot;smooth&quot;});} else {document.querySelector(`[id='${decodeURIComponent(id)}']`).scrollIntoView();}if (id === &quot;top&quot;) {history.replaceState(null, null, &quot; &quot;);} else {history.pushState(null, null, `#${id}`);}});});&lt;/script&gt;{{- if (not .Site.Params.disableScrollToTop) }}&lt;script&gt;var mybutton = document.getElementById(&quot;top-link&quot;);window.onscroll = function () {if (document.body.scrollTop &gt; 800 || document.documentElement.scrollTop &gt; 800) {mybutton.style.visibility = &quot;visible&quot;;mybutton.style.opacity = &quot;1&quot;;} else {mybutton.style.visibility = &quot;hidden&quot;;mybutton.style.opacity = &quot;0&quot;;}};&lt;/script&gt;{{- end }}{{- if (not .Site.Params.disableThemeToggle) }}&lt;script&gt;document.getElementById(&quot;theme-toggle&quot;).addEventListener(&quot;click&quot;, () =&gt; {if (document.body.className.includes(&quot;dark&quot;)) {document.body.classList.remove('dark');localStorage.setItem(&quot;pref-theme&quot;, 'light');} else {document.body.classList.add('dark');localStorage.setItem(&quot;pref-theme&quot;, 'dark');}})&lt;/script&gt;{{- end }}
